module.exports = function(name, callback) {
    console.log('looking for universe ', name);
    if (!universes.hasOwnProperty(name)) {
        universes[name] = require(name);
    }
    var u = universes[name];
    console.log(__filename, ': returning universe ', name, ': ', u.name);
    callback(u);
}

var universes = {};